import React, { useState, useEffect } from 'react';
import './App.css';
import { Button, FormControl, InputLabel, Input } from '@material-ui/core';
import Todo from './Todo.js'
import db from './firebase.js'

function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');

  // when the app load , we need to listen to the database and fetch new todos as they get added/removed

  useEffect(() => {
    // this code fire when the app.js load
    db.collection('todos').onSnapshot(snapshot => {
      setTodos(snapshot.docs.map(doc => doc.data().todo))
    })
  }, [])

  const addTodo = (event) =>{
    event.preventDefault();
    setTodos([...todos, input]);
    setInput('');
  }
  return (
    <div className="App">
      <h1>Welcome to Todo-App</h1>
      <form>
        <FormControl>
          <InputLabel>Write a todo</InputLabel>
          <Input value={input} onChange={event=>setInput(event.target.value)}/>
        </FormControl>
        <Button disabled={!input} color="primary" type="submit" variant="contained" onClick={addTodo}>Add Todo</Button>
      </form>
      <ul>
        {todos.map(todo =>(
          <Todo todo={todo}/>
        ))}
      </ul>
    </div>
  );
}

export default App;
