import React, { useState } from 'react';
import './App.css';

function App() {
  const [todos, setTodos] = useState(['himashu','rajput']);
  const [input, setInput] = useState('');
  return (
    <div className="App">
      <h1>Welcome to Todo-App</h1>
      <input value={input} />
      <button>Add Todo</button>
      <ul>
        {todos.map(todo =>(
          <li>{todo}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
